const nameLinesAll=document.querySelectorAll("p.name-line"),removeAnim=()=>{[].forEach.call(nameLinesAll,e=>{e.classList.remove("active")})};nameLinesAll.forEach((e,s)=>{e.addEventListener("click",()=>{e.classList.add("active"),setTimeout((function(){e.classList.remove("active")}),3100)}),e.addEventListener("mouseover",()=>{e.classList.add("active")}),e.addEventListener("mouseout",()=>{setTimeout((function(){e.classList.remove("active")}),3100)})});